import React,{ useState, useEffect } from "react";
import "./App.css";
import * as BooksAPI from "./BooksAPI.js";
import BookShelvesDisplay from './BookShelvesDisplay.js';


function App() {
  const [books, setBooks] = useState([]);

  const [showSearchPage, setShowSearchpage] = useState(false);

  useEffect(()=>{
      const getBooks = async()=> {
      const res = await BooksAPI.getAll();
      setBooks(res);

    };
    getBooks();}
  ,[]);


  const onUpdateShelf = (book,newShelf)=>{

    const updateBooks = async()=> {
      const res = await BooksAPI.update(book,newShelf);
      const res2 = await BooksAPI.getAll();
      setBooks(res2);
    };
    updateBooks();
  }
  return (
    <div className="app">
    {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : ( 

        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>

          <BookShelvesDisplay books={books} onUpdateShelf={onUpdateShelf}></BookShelvesDisplay>


        </div>
      )}
    </div>
  );
}

export default App;
