import { useEffect, useState } from "react";
import * as BooksAPI from "./BooksAPI.js";
import * as Book from "./Book.js";
import { Link } from "react-router-dom";
const BookSearch = ({books, onUpdateBook}) => {


    const [resBooks, setResBooks] = useState([])

    const [query, setQuery] = useState("")


    const updateQuery = (query) => {
        setQuery(query.trim());
      };


    useEffect((query)=>{

        const getBooks = async ()=>{
            const res = await BooksAPI.search(String(query));
            console.log(res);
            res.error !== 'empty query'?

            compareBooks(res,books):
            setResBooks(res);

        };

        getBooks(); 

    },[books]);

    const compareBooks = (resBooks,books) =>{

        const map = new Map();

        books.map((book)=>map.set(book.id, book));

        resBooks.map((book)=>{
            if (map.has(book.id)){
                book.shelf = map(book.id).shelf
            }
            else{
                book.shelf = 'none'
            }
        })



    };


    return(

    <div className="search-books">
        <div className="search-books-bar">
            <Link
            to="/"
            >
            Close
            </Link>
            <div className="search-books-input-wrapper">
            <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                value = {query}
                onClick={updateQuery}

            />
            </div>
        </div>
        <div className="search-books-results">
            <ol className="books-grid"></ol>
        </div>

        <div className="search-books-result">
            <ol classNanme="books-grid">
            {   resBooks.length!=0 ? (
                resBooks.map((book) => (
            <li key={book.id}>
              <Book
                key={book.id}
                bookData={book}
                onBookUpdate={onUpdateBook}
              ></Book>
            </li>
          ))
        ) : (
          <div>
            <p>No Books found</p>
          </div>
        )}


            </ol>
        </div>
    </div>
    );
    };

export default BookSearch;
