import * as BookShelfCategory from './bookShelfCategory.js';

const BookShelvesDisplay = ({books,
    onUpdateShelf
})=>{


    const shelfCategories = [
        {
          title: 'Currently Reading',
          category: 'currentlyReading',
          id: 'currentlyReadingShelfTray',
        },
        {
          title: 'Want to Read',
          category: 'wantToRead',
          id: 'wantToReadShelfTray',
        },
        {   title: 'Read', 
            category: 'read', 
            id: 'readShelfTray' },
      ];

    return(

        <div className="list-books-content">
            <div className="bookshelf">
            {shelfCategories.map((shelfCategory)=>{
                <BookShelfCategory books={books} shelf={shelfCategory.title} onUpdateShelf={onUpdateShelf}></BookShelfCategory>
            })}
            </div>
            <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>


    );
}


export default BookShelvesDisplay;
